package com.msaggik.playlistmakerimport android.os.Bundleimport android.view.Viewimport android.widget.Buttonimport android.widget.Toastimport androidx.activity.enableEdgeToEdgeimport androidx.appcompat.app.AppCompatActivityimport androidx.core.view.ViewCompatimport androidx.core.view.WindowInsetsCompatclass MainActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        enableEdgeToEdge()        setContentView(R.layout.activity_main)        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main)) { v, insets ->            val systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars())            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom)            insets        }        val buttonSearch = findViewById<Button>(R.id.button_search)        val buttonMedia = findViewById<Button>(R.id.button_media)        val buttonSetting = findViewById<Button>(R.id.button_setting)        buttonSearch.setOnClickListener {            Toast.makeText(this@MainActivity, "Нажата кнопка поиска", Toast.LENGTH_SHORT).show()        }        buttonMedia.setOnClickListener(listener)        buttonSetting.setOnClickListener(listener)    }    private val listener: View.OnClickListener = object: View.OnClickListener {        override fun onClick(p0: View?) {            when(p0?.id) {                R.id.button_media -> Toast.makeText(this@MainActivity, "Нажата кнопка медиа", Toast.LENGTH_SHORT).show()                R.id.button_setting -> Toast.makeText(this@MainActivity, "Нажата кнопка настроек", Toast.LENGTH_SHORT).show()            }        }    }}